#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\danni\ai-suggestions-prototype\ai-codegen-extension\node_modules\.pnpm\@vscode+vsce@3.6.0\node_modules\@vscode\vsce\node_modules;C:\Users\danni\ai-suggestions-prototype\ai-codegen-extension\node_modules\.pnpm\@vscode+vsce@3.6.0\node_modules\@vscode\node_modules;C:\Users\danni\ai-suggestions-prototype\ai-codegen-extension\node_modules\.pnpm\@vscode+vsce@3.6.0\node_modules;C:\Users\danni\ai-suggestions-prototype\ai-codegen-extension\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/proc/cygdrive/c/Users/danni/ai-suggestions-prototype/ai-codegen-extension/node_modules/.pnpm/@vscode+vsce@3.6.0/node_modules/@vscode/vsce/node_modules:/proc/cygdrive/c/Users/danni/ai-suggestions-prototype/ai-codegen-extension/node_modules/.pnpm/@vscode+vsce@3.6.0/node_modules/@vscode/node_modules:/proc/cygdrive/c/Users/danni/ai-suggestions-prototype/ai-codegen-extension/node_modules/.pnpm/@vscode+vsce@3.6.0/node_modules:/proc/cygdrive/c/Users/danni/ai-suggestions-prototype/ai-codegen-extension/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../vsce" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../vsce" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../vsce" $args
  } else {
    & "node$exe"  "$basedir/../../vsce" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
